# Exercice 2
# Initialiser un compteur pour le nombre d'éléments contenant à la fois des chiffres et des lettres
nombre_elements_mixtes = 0

# Itérer à travers chaque élément du tableau
journalist.each do |element|
  # Vérifier si l'élément contient à la fois des chiffres et des lettres
  if element.match?(/\d/) && element.match?(/[a-zA-Z]/)
    # Si c'est le cas, incrémenter le compteur
    nombre_elements_mixtes += 1
  end
end

# Afficher le nombre total d'éléments contenant à la fois des chiffres et des lettres
puts "Le nombre total d'éléments contenant à la fois des chiffres et des lettres dans le tableau est : #{nombre_elements_mixtes}"

# Exercice 3

# Convertir le prénom "aude" en une expression régulière insensible à la casse
regex_aude = /aude/i

# Initialiser un compteur pour le nombre d'éléments contenant "aude" à la suite
nombre_elements_aude = 0

# Itérer à travers chaque élément du tableau
journalist.each do |element|
  # Vérifier si l'élément contient "aude" à la suite
  if element.match?(regex_aude)
    # Si c'est le cas, incrémenter le compteur
    nombre_elements_aude += 1
  end
end

# Afficher le nombre total d'éléments contenant "aude" à la suite
puts "Le nombre total d'éléments contenant les lettres 'aude' à la suite dans le tableau est : #{nombre_elements_aude}"

# Exercice 4

# Initialiser un compteur pour le nombre d'éléments commençant par une majuscule après le @
nombre_elements_majuscule = 0

# Itérer à travers chaque élément du tableau
journalist.each do |element|
  # Vérifier si l'élément commence par un @ suivi d'une majuscule
  if element.match?(/@([A-Z])/)
    # Si c'est le cas, incrémenter le compteur
    nombre_elements_majuscule += 1
  end
end

# Afficher le nombre total d'éléments commençant par une majuscule après le @
puts "Le nombre total d'éléments commençant par une majuscule après le @ dans le tableau est : #{nombre_elements_majuscule}"

# Exercice 5

# Initialiser un compteur pour le nombre d'éléments contenant au moins une majuscule
nombre_elements_majuscule = 0

# Itérer à travers chaque élément du tableau
journalist.each do |element|
  # Vérifier si l'élément contient au moins une lettre majuscule
  if element.match?(/[A-Z]/)
    # Si c'est le cas, incrémenter le compteur
    nombre_elements_majuscule += 1
  end
end

# Afficher le nombre total d'éléments contenant au moins une majuscule
puts "Le nombre total d'éléments contenant au moins une majuscule dans le tableau est : #{nombre_elements_majuscule}"

# Exercice 6
# Initialiser un compteur pour le nombre total de caractères underscore _
nombre_underscore = 0

# Itérer à travers chaque pseudonyme du tableau
journalist.each do |pseudo|
  # Utiliser la méthode `count` pour compter le nombre de underscores dans chaque pseudonyme
  nombre_underscore += pseudo.count("_")
end

# Afficher le nombre total de caractères underscore _ dans tous les pseudonymes
puts "Le nombre total de caractères underscore _ dans tous les pseudonymes est : #{nombre_underscore}"

